mapper.select:
SELECT SOLIRECLA.FTC_CVE_SOLICITUD CVESOLICITUD, SOLIRECLA.FTC_FOLIO FOLIO, SOLIRECLA.FTD_FEH_CAP FECHASOLICITUD, ORIGEN.FCC_VALOR ORIGEN, SOLIRECLA.FTN_NUM_CTA_INVDUAL NUMCUENTA, SOLIRECLA.FTC_USU_CRE NOMBRE, SOLIRECLA.FTN_MONTO_ORI_RECLASIFICA IMPORTE, ESTATUS.FCC_VALOR ESTATUS
FROM PROFAPOVOL.TTAFOAVOL_SOL_RECLASIFICACION SOLIRECLA INNER JOIN TCCRXGRAL_CAT_CATALOGO ORIGEN ON ORIGEN.FCN_ID_CAT_CATALOGO=SOLIRECLA.FCN_ID_ORIGEN_CAPTURA INNER JOIN TCCRXGRAL_CAT_CATALOGO ESTATUS ON ESTATUS.FCN_ID_CAT_CATALOGO=SOLIRECLA.FCN_ID_ESTATUS_SOLICITUD
FCN_ID_ORIGEN_CAPTURA=#{origen,jdbcType=DECIMAL}
AND FCN_ID_ESTATUS_SOLICITUD=#{estatus,jdbcType=DECIMAL}
AND to_date(trunc(FTD_FEH_CAP),'dd-mm-yy') BETWEEN TO_DATE(#{fechaInicio},'yy-mm-dd') and TO_DATE(#{fechaInicio},'yy-mm-dd')
AND to_date(trunc(FTD_FEH_CAP),'dd-mm-yy') BETWEEN TO_DATE(#{fechaFin},'yy-mm-dd') and TO_DATE(#{fechaFin},'yy-mm-dd')

mapper.insert:
INSERT INTO TTAFOAVOL_RECLA_DIVERSIFICA
FTC_CVE_SOLICITUD, FCN_ID_SIEFORE, FCN_ID_SUBCTA, FTN_PESOS, FTN_ACCIONES, FTN_PORCENTAJE, FCN_ID_VALOR_ACCION, FCN_ID_TIPO_MOV, FCN_ID_FONDO_APP, FTN_PLAZO, FTN_DEDUCIBLE, FTD_FEH_CRE, FTC_USU_CRE
#{cveSolicitud,jdbcType=VARCHAR}, #{idSiefore,jdbcType=DECIMAL}, #{idSubcta,jdbcType=DECIMAL}, #{pesos,jdbcType=DECIMAL}, #{acciones,jdbcType=DECIMAL}, #{porcentaje,jdbcType=DECIMAL}, #{idValorAccion,jdbcType=DECIMAL}, #{idTipoMov,jdbcType=DECIMAL}, #{idFondoApp,jdbcType=DECIMAL}, #{plazo,jdbcType=DECIMAL}, #{deducible,jdbcType=DECIMAL}, SYSTIMESTAMP, #{usuCre,jdbcType=VARCHAR}

INSERT INTO TTAFOAVOL_RECLA_SALDO_ORIGINAL
FTC_CVE_SOLICITUD, FCN_ID_SIEFORE, FCN_ID_SUBCTA, FTN_PESOS, FTN_ACCIONES, FCN_ID_VALOR_ACCION, FTD_FEH_CRE, FTC_USU_CRE
#{cveSolicitud,jdbcType=VARCHAR}, #{idSiefore,jdbcType=DECIMAL}, #{idSubcta,jdbcType=DECIMAL}, #{pesos,jdbcType=DECIMAL}, #{acciones,jdbcType=DECIMAL}, #{idValorAccion,jdbcType=DECIMAL}, SYSTIMESTAMP, #{usuCre,jdbcType=VARCHAR}

INSERT INTO TTAFOAVOL_RECLA_INDICADORES
FTC_CVE_SOLICITUD, FCN_ID_INDICADOR, FCC_VALOR, FTD_FEH_CRE, FTC_USU_CRE
#{cveSolicitud,jdbcType=VARCHAR}, #{idIndicador,jdbcType=NUMERIC}, #{valor,jdbcType=VARCHAR}, SYSTIMESTAMP, #{usuCre,jdbcType=VARCHAR}

INSERT INTO TTAFOAVOL_RECLA_ESTATUS_SOL
FTC_CVE_SOLICITUD, FCN_ID_ESTATUS_SOLICITUD, FTD_FEH_CRE, FTC_USU_CRE
#{cveSolicitud,jdbcType=VARCHAR}, #{idEstatusSolicitud,jdbcType=DECIMAL}, SYSTIMESTAMP, #{usuCre,jdbcType=VARCHAR}

mapper.update:
UPDATE TTAFOAVOL_SOL_RECLASIFICACION SET FCN_ID_ESTATUS_SOLICITUD = #{idEstatusSolicitud,jdbcType=DECIMAL},
FCN_ID_MOTIVO_RECHAZO = #{idMotivoRechazo,jdbcType=DECIMAL},
FTD_FEH_ACT = SYSTIMESTAMP, FTC_USU_ACT = #{usuCre,jdbcType=VARCHAR}
FTC_CVE_SOLICITUD = #{cveSolicitud,jdbcType=VARCHAR}
