mapper.select:
select BANC.FTN_ID_BANCO, BANC.FTC_FOLIO, BANC.FTN_REF_CUENTA, TO_CHAR(BANC.FTN_AUT_BANCO) AS FTN_AUT_BANCO, BANC.FTN_REF_SUBCTA_APOVOL, ( SELECT DISTINCT(TMC_DESC_ITGY) FROM TTAFOAVOL_BANCOS B LEFT JOIN TMSISGRAL_MAP_NCI_ITGY A ON A.TMN_CVE_NCI = B.FTN_REF_SUBCTA_APOVOL WHERE BANC.FTN_REF_SUBCTA_APOVOL = B.FTN_REF_SUBCTA_APOVOL AND A.TMC_USO = 'REFERENCIAS DEPOSITOS APOVOL') AS TMC_DESC_ITGY, BANC.FTN_IMPORTE, BANC.FTN_CVE_BANCO, CVE_BANC.FCC_VALOR AS FCC_VALOR_CLAVE_BANCO, BANC.FTD_FEH_CARGA, BANC.FTN_ORIGEN_APORTACION, ORIGEN.FCC_VALOR AS FCC_VALOR_ORIGEN_APORTACION, BANC.FTN_IND_SBC, SBC.FCC_VALOR AS FCC_VALOR_IND_SBC, BANC.FTN_NUM_CTA_INVDUAL, BANC.FTC_CURP, BANC.FTN_REG_CONCILIADO, BANC.FTN_ESTATUS_IDC, IDC.FCC_VALOR AS FCC_VALOR_ESTATUS_IDC, BANC.FTN_ID_EMPRESA, BANC.FTN_TIPO_ID, FA.FTC_FOLIO AS FA_FTC_FOLIO, FA.FTD_FEH_CRE AS FA_FTD_FEH_CRE, FA.FTC_USU_CRE AS FA_FTC_USU_CRE, FA.FTD_FEH_ACT AS FA_FTD_FEH_ACT, FA.FTC_USU_ACT AS FA_FTC_USU_ACT from TTAFOAVOL_BANCOS BANC LEFT JOIN TRAFOAVOL_FOLIO_BANCO FA ON FA.FTN_ID_BANCO = BANC.FTN_ID_BANCO LEFT JOIN TCCRXGRAL_CAT_CATALOGO CVE_BANC ON CVE_BANC.FCN_ID_CAT_CATALOGO= BANC.FTN_CVE_BANCO LEFT JOIN TCCRXGRAL_CAT_CATALOGO ORIGEN ON ORIGEN.FCN_ID_CAT_CATALOGO = BANC.FTN_ORIGEN_APORTACION LEFT JOIN TCCRXGRAL_CAT_CATALOGO IDC ON IDC.FCN_ID_CAT_CATALOGO =BANC.FTN_ESTATUS_IDC LEFT JOIN TCCRXGRAL_CAT_CATALOGO SBC ON SBC.FCN_ID_CAT_CATALOGO = BANC.FTN_IND_SBC
AND BANC.FTN_REG_CONCILIADO = #{regConciliado}
AND BANC.FTN_CVE_BANCO = #{claveBanco.id}
AND BANC.FTD_FEH_CARGA BETWEEN trunc(#{fechaInicio,jdbcType=TIMESTAMP}) AND trunc(#{fechaFinal,jdbcType=TIMESTAMP})+.99999
AND BANC.FTC_FOLIO = #{folio}
AND BANC.FTN_NUM_CTA_INVDUAL = #{numCuentaIndividual}
AND BANC.FTN_CVE_BANCO = #{claveBanco.id}
AND BANC.FTN_TIPO_ID = #{tipoIdentificacion}
AND BANC.FTN_ORIGEN_APORTACION IN
#{origen,jdbcType=DECIMAL}
AND BANC.FTN_ID_BANCO = #{idBanco}
AND BANC.FTN_IMPORTE>0 AND BANC.FTN_ORIGEN_APORTACION not in (288)

SELECT BANC.FTN_ID_BANCO, BANC.FTC_FOLIO, BANC.FTC_FOLIO_REL, BANC.FTN_REF_CUENTA, TO_CHAR(BANC.FTN_AUT_BANCO) AS FTN_AUT_BANCO, BANC.FTN_REF_SUBCTA_APOVOL, (SELECT DISTINCT(TMC_DESC_ITGY) FROM TTAFOAVOL_BANCOS B LEFT JOIN TMSISGRAL_MAP_NCI_ITGY A ON A.TMN_CVE_NCI = B.FTN_REF_SUBCTA_APOVOL WHERE BANC.FTN_REF_SUBCTA_APOVOL = B.FTN_REF_SUBCTA_APOVOL AND A.TMC_USO = 'REFERENCIAS DEPOSITOS APOVOL') AS TMC_DESC_ITGY, BANC.FTN_IMPORTE, BANC.FTN_CVE_BANCO, CVE_BANC.FCC_VALOR AS FCC_VALOR_CLAVE_BANCO, BANC.FTD_FEH_CARGA, BANC.FTN_ORIGEN_APORTACION, ORIGEN.FCC_VALOR AS FCC_VALOR_ORIGEN_APORTACION, BANC.FTN_IND_SBC, SBC.FCC_VALOR AS FCC_VALOR_IND_SBC, BANC.FTN_NUM_CTA_INVDUAL, BANC.FTC_CURP, BANC.FTN_REG_CONCILIADO, BANC.FTN_ESTATUS_IDC, IDC.FCC_VALOR AS FCC_VALOR_ESTATUS_IDC, BANC.FTN_ID_EMPRESA, BANC.FTN_ESTATUS_CONV,BANC.FTN_MOTIVO_RECHAZO,MOR.FCC_VALOR AS FCC_VALOR_MOTIVO_RECHAZO, BANC.FTD_FEH_DEPOSITO FROM TTAFOAVOL_BANCOS BANC INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO AF ON AF.FTC_FOLIO=BANC.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO FOL ON FOL.FTN_ID_ARCHIVO=AF.FTN_ID_ARCHIVO LEFT JOIN TCCRXGRAL_CAT_CATALOGO CVE_BANC ON CVE_BANC.FCN_ID_CAT_CATALOGO = BANC.FTN_CVE_BANCO LEFT JOIN TCCRXGRAL_CAT_CATALOGO ORIGEN ON ORIGEN.FCN_ID_CAT_CATALOGO = BANC.FTN_ORIGEN_APORTACION LEFT JOIN TCCRXGRAL_CAT_CATALOGO IDC ON IDC.FCN_ID_CAT_CATALOGO = BANC.FTN_ESTATUS_IDC LEFT JOIN TCCRXGRAL_CAT_CATALOGO SBC ON SBC.FCN_ID_CAT_CATALOGO = BANC.FTN_IND_SBC LEFT JOIN TCCRXGRAL_CAT_CATALOGO MOR ON MOR.FCN_ID_CAT_CATALOGO = BANC.FTN_MOTIVO_RECHAZO LEFT JOIN TTAFOGRAL_IND_CTA_INDV CTA ON BANC.FTN_NUM_CTA_INVDUAL = CTA.FTN_NUM_CTA_INVDUAl AND FFN_ID_CONFIG_INDI=2
AND BANC.FTN_CVE_BANCO = #{claveBanco.id}
AND FOL.FTD_FEH_ARCHIVO BETWEEN trunc(#{fechaInicio,jdbcType=TIMESTAMP}) AND trunc(#{fechaFinal,jdbcType=TIMESTAMP})+.99999
AND BANC.FTC_FOLIO = #{folio}
AND BANC.FTN_NUM_CTA_INVDUAL = #{numCuentaIndividual}
AND BANC.FTN_ORIGEN_APORTACION IN
#{origen,jdbcType=DECIMAL}
AND (BANC.FTN_ESTATUS_CONV = #{estatusConvivencia.id} OR BANC.FTN_ESTATUS_IDC = #{estatusIDC.id})
AND (BANC.FTN_ESTATUS_IDC = #{estatusIDC.id})
AND (BANC.FTN_ESTATUS_CONV = #{estatusConvivencia.id})
AND BANC.FTN_ID_BANCO = #{idBanco}
AND BANC.FTN_IMPORTE>0
UNION SELECT BANC.FTN_ID_BANCO, BANC.FTC_FOLIO, BANC.FTC_FOLIO_REL, BANC.FTN_REF_CUENTA, TO_CHAR(BANC.FTN_AUT_BANCO) AS FTN_AUT_BANCO, BANC.FTN_REF_SUBCTA_APOVOL, (SELECT DISTINCT(TMC_DESC_ITGY) FROM TTAFOAVOL_BANCOS B LEFT JOIN TMSISGRAL_MAP_NCI_ITGY A ON A.TMN_CVE_NCI = B.FTN_REF_SUBCTA_APOVOL WHERE BANC.FTN_REF_SUBCTA_APOVOL = B.FTN_REF_SUBCTA_APOVOL AND A.TMC_USO = 'REFERENCIAS DEPOSITOS APOVOL') AS TMC_DESC_ITGY, BANC.FTN_IMPORTE, BANC.FTN_CVE_BANCO, CVE_BANC.FCC_VALOR AS FCC_VALOR_CLAVE_BANCO, BANC.FTD_FEH_CARGA, BANC.FTN_ORIGEN_APORTACION, ORIGEN.FCC_VALOR AS FCC_VALOR_ORIGEN_APORTACION, BANC.FTN_IND_SBC, SBC.FCC_VALOR AS FCC_VALOR_IND_SBC, BANC.FTN_NUM_CTA_INVDUAL, BANC.FTC_CURP, BANC.FTN_REG_CONCILIADO, BANC.FTN_ESTATUS_IDC, IDC.FCC_VALOR AS FCC_VALOR_ESTATUS_IDC, BANC.FTN_ID_EMPRESA, BANC.FTN_ESTATUS_CONV,BANC.FTN_MOTIVO_RECHAZO,MOR.FCC_VALOR AS FCC_VALOR_MOTIVO_RECHAZO, BANC.FTD_FEH_DEPOSITO FROM TTAFOAVOL_BANCOS BANC INNER JOIN TRAFOGRAL_ARCHIVO_FOLIO AF ON AF.FTC_FOLIO=BANC.FTC_FOLIO INNER JOIN TTAFOGRAL_ARCHIVO FOL ON FOL.FTN_ID_ARCHIVO=AF.FTN_ID_ARCHIVO LEFT JOIN TCCRXGRAL_CAT_CATALOGO CVE_BANC ON CVE_BANC.FCN_ID_CAT_CATALOGO = BANC.FTN_CVE_BANCO LEFT JOIN TCCRXGRAL_CAT_CATALOGO ORIGEN ON ORIGEN.FCN_ID_CAT_CATALOGO = BANC.FTN_ORIGEN_APORTACION LEFT JOIN TCCRXGRAL_CAT_CATALOGO IDC ON IDC.FCN_ID_CAT_CATALOGO = BANC.FTN_ESTATUS_IDC LEFT JOIN TCCRXGRAL_CAT_CATALOGO SBC ON SBC.FCN_ID_CAT_CATALOGO = BANC.FTN_IND_SBC LEFT JOIN TCCRXGRAL_CAT_CATALOGO MOR ON MOR.FCN_ID_CAT_CATALOGO = BANC.FTN_MOTIVO_RECHAZO LEFT JOIN TTAFOGRAL_IND_CTA_INDV CTA ON BANC.FTN_NUM_CTA_INVDUAL = CTA.FTN_NUM_CTA_INVDUAl AND FFN_ID_CONFIG_INDI=2
AND BANC.FTN_REG_CONCILIADO in (0)
AND BANC.FTN_CVE_BANCO = #{claveBanco.id}
AND FOL.FTD_FEH_ARCHIVO BETWEEN trunc(#{fechaInicio,jdbcType=TIMESTAMP}) AND trunc(#{fechaFinal,jdbcType=TIMESTAMP})+.99999
AND BANC.FTC_FOLIO = #{folio}
AND BANC.FTN_NUM_CTA_INVDUAL = #{numCuentaIndividual}
AND BANC.FTN_ORIGEN_APORTACION IN (758)
AND BANC.FTN_ID_BANCO = #{idBanco}
AND BANC.FTN_IMPORTE>0
ORDER BY FTD_FEH_CARGA

