mapper.select:
select row_number() over ( order by ARGEN.FTN_CVE_BANCO, DET.FTN_ID_TIPO_CTA, DET.FTN_CVE_BANCO, T_A_CAT.FCC_VALOR, T_C_CAT.FCC_VALOR, C_B_CAT.FCC_VALOR) AS FLN_ID_ARCHIVO_DOMI, max(ARGEN.FLD_FEH_INI_GEN) as FLD_FEH_INI_GEN, max(ARGEN.FLD_FEH_FIN_GEN) AS FLD_FEH_FIN_GEN, ARGEN.FTN_CVE_BANCO AS FTN_TIPO_ARCHIVO, T_A_CAT.FCC_VALOR AS FTN_VALOR_TIPO_ARCHIVO, DET.FTN_ID_TIPO_CTA, T_C_CAT.FCC_VALOR AS FCC_VALOR_TIPO_CTA, DET.FTN_CVE_BANCO, C_B_CAT.FCC_VALOR AS FCC_VALOR_CLAVE_BANCO from TLAFOAVOL_ARCHIVOS_DOMI_GEN ARGEN join TLAFOAVOL_DETALLE_CTAS_BANCO DET on ARGEN.FLN_ID_ARCHIVO_DOMI = DET.FLN_ID_ARCHIVO_DOMI left join TCCRXGRAL_CAT_CATALOGO T_A_CAT on T_A_CAT.FCN_ID_CAT_CATALOGO = ARGEN.FTN_CVE_BANCO left join TCCRXGRAL_CAT_CATALOGO T_C_CAT on T_C_CAT.FCN_ID_CAT_CATALOGO = DET.FTN_ID_TIPO_CTA left join TCCRXGRAL_CAT_CATALOGO C_B_CAT on C_B_CAT.FCN_ID_CAT_CATALOGO = DET.FTN_CVE_BANCO
AND DET.FTN_ID_TIPO_CTA =#{tipoCuenta.id}
DET.FTN_CVE_BANCO =#{idBanco}
AND DET.FTN_ORIGEN_APORTACION =#{idOrigenSolicitud}
AND FLN_ESTATUS > 0
group by ARGEN.FTN_CVE_BANCO, DET.FTN_ID_TIPO_CTA, DET.FTN_CVE_BANCO, T_A_CAT.FCC_VALOR,T_C_CAT.FCC_VALOR,C_B_CAT.FCC_VALOR

SELECT count(gen.FLC_NOMBRE_ARCHIVO)+1 AS CONSECUTIVO FROM TLAFOAVOL_ARCHIVOS_DOMI_GEN gen LEFT join TLAFOAVOL_DETALLE_CTAS_BANCO DET on gen.FLN_ID_ARCHIVO_DOMI = DET.FLN_ID_ARCHIVO_DOMI
GEN.FLD_FEH_CRE BETWEEN TRUNC(SYSDATE) AND TRUNC(SYSDATE)+.99999
AND DET.FTN_ORIGEN_APORTACION =#{idOrigenSolicitud}

SELECT FCC_VALOR, FCC_DESC 
FROM TCCRXGRAL_CAT_CATALOGO
WHERE FCN_ID_CAT_CATALOGO = #{idCatalogo, jdbcType = DECIMAL}
