mapper.select:
SELECT CIFLIQ.FTN_NUM_CTA_INVDUAL FTN_NUM_CTA_INVDUAL, CIFLIQ.FTC_NSS FTC_NSS, CIFLIQ.FTN_IMPORTE FTN_IMPORTE, ROWNUM FTN_NO_CARGO, CIFLIQ.FTD_FEH_CONCILIACION FTD_FEH_CONCILIACION, CIFLIQ.FTN_EXCLUIR FTN_EXCLUIR FROM ttafoavol_cifras_liquidacion CIFLIQ
WHERE 1=1
AND FTN_NUM_CTA_INVDUAL = #{cuenta,jdbcType=DECIMAL}
AND FTC_NSS = #{nss,jdbcType=VARCHAR}
AND FTC_CURP = #{curp,jdbcType=VARCHAR}
order by CIFLIQ.FTD_FEH_CONCILIACION, FTN_NO_CARGO
