mapper.select:
SELECT SOLICITUD.FTC_CVE_SOLICITUD CLAVE, SOLICITUD.FTC_FOLIO FOLIO, SOLICITUD.FTD_FEH_CAP FECHASOLICITUD, ORIGEN.FCC_VALOR DESCORIGEN, ORIGEN.FCN_ID_CAT_CATALOGO IDORIGEN, SOLICITUD.FTN_NUM_CTA_INVDUAL CUENTA, SOLICITANTE.FTC_NOMBRE_CTE NOMBRE, SOLICITANTE.FTC_AP_PATERNO_CTE APATERNO, SOLICITANTE.FTC_AP_MATERNO_CTE AMATERNO, SOLICITUD.FTN_MONTO_RECLASIF IMPORTE, ESTATUS.FCC_VALOR ESTATUS, ESTATUS.FCN_ID_CAT_CATALOGO IDESTATUS, SOLICITANTE.FTC_CURP CURP, SOLICITANTE.FTC_NSS NSS, FONDO.FCC_VALOR FONDO,
SOLICITUD.FTN_MONTO_RECLASIF MONTO
FROM PROFAPOVOL.TTAFOAVOL_SOL_RECLASIFICACION SOLICITUD INNER JOIN PROFAPOVOL.TTAFOAVOL_RECLA_SOLICITANTE SOLICITANTE ON SOLICITUD.FTN_NUM_CTA_INVDUAL = SOLICITANTE.FTN_NUM_CTA_INVDUAL INNER JOIN TTAFOAVOL_RECLA_DIVERSIFICA DIVERSIFICA ON SOLICITUD.FTC_CVE_SOLICITUD = DIVERSIFICA.FTC_CVE_SOLICITUD INNER JOIN TCCRXGRAL_CAT_CATALOGO ORIGEN ON SOLICITUD.FCN_ID_ORIGEN_CAPTURA = ORIGEN.FCN_ID_CAT_CATALOGO INNER JOIN TCCRXGRAL_CAT_CATALOGO ESTATUS ON SOLICITUD.FCN_ID_ESTATUS_SOLICITUD = ESTATUS.FCN_ID_CAT_CATALOGO INNER JOIN TCCRXGRAL_CAT_CATALOGO FONDO ON SOLICITUD.FCN_ID_FONDO_APP = FONDO.FCN_ID_CAT_CATALOGO
DIVERSIFICA.FCN_ID_TIPO_MOV=180
AND SOLICITANTE.FTC_NSS=#{nss,jdbcType=VARCHAR}
AND SOLICITANTE.FTC_CURP=#{curp,jdbcType=VARCHAR}
AND SOLICITUD.FTN_NUM_CTA_INVDUAL=#{cuenta,jdbcType=DECIMAL}
AND SOLICITUD.FCN_ID_ORIGEN_CAPTURA=#{origen,jdbcType=DECIMAL}
AND SOLICITUD.FCN_ID_ESTATUS_SOLICITUD=#{estatus,jdbcType=DECIMAL}
AND SOLICITUD.FTD_FEH_CAP BETWEEN trunc(#{fechaInicio}) AND trunc(#{fechaFin})+.99999
AND SOLICITUD.FTD_FEH_CAP >= to_timestamp(#{fechaInicio,jdbcType=DATE}, 'dd/MM/yy hh24:mi:ss.FF9')
AND SOLICITUD.FTD_FEH_CAP >= to_timestamp(#{fechaFin,jdbcType=DATE}, 'dd/MM/yy hh24:mi:ss.FF9')
ORDER BY FTD_FEH_CAP DESC

SELECT RECLASIFICACION.FTD_FEH_CAP AS FECHCAP, MOVS.FTD_FEH_LIQUIDACION FEHlIQ, SOLICITANTE.FTN_NUM_CTA_INVDUAL AS CUENTAINDI, SOLICITANTE.FTC_NSS AS NSS, SOLICITANTE.FTC_CURP AS CURP, SOLICITANTE.FTC_NOMBRE_CTE ||' '|| SOLICITANTE.FTC_AP_PATERNO_CTE ||' '|| SOLICITANTE.FTC_AP_MATERNO_CTE AS NOMBRE, RECLASIFICACION.FTC_CVE_SOLICITUD AS CLAVESOLI, RECLASIFICACION.FTC_FOLIO FOLIO, ESTATUS.FCC_VALOR AS ESTAUSSOLI, ORIGEN.FCC_VALOR AS ORIGEN, TIPORECLA.FCC_VALOR AS TIPOMONTO,
RECLASIFICACION.FTC_USU_CRE AS USUARIOCAP, RECLASIFICACION.FTD_FEH_ACT AS FECHACT, RECLASIFICACION.FTC_USU_ACT AS USUARIOACT
FROM TTAFOAVOL_SOL_RECLASIFICACION RECLASIFICACION
INNER JOIN TTAFOAVOL_RECLA_SOLICITANTE SOLICITANTE ON RECLASIFICACION.FTN_NUM_CTA_INVDUAL = SOLICITANTE.FTN_NUM_CTA_INVDUAL INNER JOIN TTAFOAVOL_RECLA_DIVERSIFICA DIVERSIFICA ON RECLASIFICACION.FTC_CVE_SOLICITUD = DIVERSIFICA.FTC_CVE_SOLICITUD INNER JOIN TCCRXGRAL_CAT_CATALOGO ESTATUS ON RECLASIFICACION.FCN_ID_ESTATUS_SOLICITUD = ESTATUS.FCN_ID_CAT_CATALOGO INNER JOIN TCCRXGRAL_CAT_CATALOGO ORIGEN ON RECLASIFICACION.FCN_ID_ORIGEN_CAPTURA = ORIGEN.FCN_ID_CAT_CATALOGO LEFT JOIN TCCRXGRAL_CAT_CATALOGO TIPORECLA ON RECLASIFICACION.FCN_ID_TIPO_RECLASIFICACION = TIPORECLA.FCN_ID_CAT_CATALOGO LEFT JOIN (
SELECT /*+ PARALLEL(10) */ FTC_FOLIO,FTD_FEH_LIQUIDACION FROM TTAFOGRAL_MOV_AVOL mavol GROUP BY FTC_FOLIO,FTD_FEH_LIQUIDACION UNION SELECT /*+ PARALLEL(10) */ FTC_FOLIO,FTD_FEH_LIQUIDACION FROM TTAFOGRAL_MOV_COMP mcomp GROUP BY FTC_FOLIO,FTD_FEH_LIQUIDACION
) MOVS ON MOVS.FTC_FOLIO = RECLASIFICACION.FTC_FOLIO
DIVERSIFICA.FCN_ID_TIPO_MOV=180
AND SOLICITANTE.FTC_NSS=#{nss,jdbcType=VARCHAR}
AND SOLICITANTE.FTC_CURP=#{curp,jdbcType=VARCHAR}
AND SOLICITANTE.FTN_NUM_CTA_INVDUAL=#{cuenta,jdbcType=DECIMAL}
AND RECLASIFICACION.FCN_ID_ORIGEN_CAPTURA=#{origen,jdbcType=DECIMAL}
AND RECLASIFICACION.FCN_ID_ESTATUS_SOLICITUD=#{estatus,jdbcType=DECIMAL}
AND RECLASIFICACION.FTD_FEH_CAP BETWEEN trunc(#{fechaInicio}) AND trunc(#{fechaFin})+.99999
AND RECLASIFICACION.FTD_FEH_CAP >= to_timestamp(#{fechaInicio,jdbcType=DATE}, 'dd/MM/yy hh24:mi:ss.FF9')
AND RECLASIFICACION.FTD_FEH_CAP >= to_timestamp(#{fechaFin,jdbcType=DATE}, 'dd/MM/yy hh24:mi:ss.FF9')
ORDER BY FTD_FEH_CAP ASC
